mapper.update:
UPDATE BBUSINESSREVIEW SET 
	CONTENT=#{bcontent}, registdate=TO_CHAR(sysdate,'YYYY-MM-DD HH24:MI:SS') 
	WHERE ESTID=#{estid} AND SALECODE=#{salecode}

UPDATE BBUSINESSREVIEW SET 
	CONTENT='-', updatedate=TO_CHAR(sysdate,'YYYY-MM-DD HH24:MI:SS') 
	WHERE ESTID=#{estid} AND SALECODE=#{salecode}
