mapper.update:
UPDATE BMENU 
	SET
		NUM = #{num}, NAME = #{name}, PRICE = #{price}, QUICKPRICE = #{quickprice},
		ETE = #{ete}, MAINCATEGORY = #{maincategory}, SUBCATEGORY = #{subcategory}
	WHERE ESTID = #{estid}

mapper.delete:
		DELETE FROM BMENU 
			WHERE 
				ESTID=#{estid} 
 			AND 
 				MAINCATEGORY=#{maincategory} 
 			AND 
 				SUBCATEGORY = #{subcategory} 
 			AND 
 				NAME= #{name};
