mapper.select:
SELECT ADDRESSC
	FROM
BDELIVERYAREA
WHERE
	ESTID = #{estid} 
		AND
	ADDRESSA = #{addressa}
		AND
	ADDRESSB = #{addressb}

mapper.update:
UPDATE BDELIVERYAREA
	SET
		ADDRESSA = #{addressa}, ADDRESSB = #{addressb}, ADDRESSC = #{addressc},
		ADDRESSD = #{addressd}, ADDRESSE = #{addressE}, ADDRESSF = #{addressf}
	WHERE ESTID = #{estid}

mapper.delete:
DELETE 
	FROM 
BDELIVERYAREA
	WHERE
ESTID = #{estid} 
	AND 
		ADDRESSA = #{addressa} 
	AND 
		ADDRESSB = #{addressb} 
	AND 
		ADDRESSC = #{addressc}
